package Algorithms;

public class CalculateTime {
    public int calculateTime(String keyboard, String word) {
        int result = 0;
        int[] arr = new int[word.length()];

        for (int i = 0; i < word.length(); i++) {

            for (int j = 0; j < keyboard.length(); j++) {
                if (keyboard.charAt(j) == word.charAt(i)) {
                    arr[i] = j;

                    if (i == 0) {
                        result += j;

                    } else {
                        result += Math.abs(j - arr[i - 1]);

                    }

                }
            }


        }

        return result;

    }

    public static void main(String[] args) {

        CalculateTime u = new CalculateTime();
        String keyboardOne = "abcdefghijklmnopqrstuvwxyz";
        String keyboardTwo = "pqrstuvwxyzabcdefghijklmno";
        String keyboardThree = "pyevcountbgjklxfqwimrdhazs";
        String wordThree = "";


        //System.out.println(u.calculateTime(keyboardOne, "cba")); //4
        //System.out.println(u.calculateTime(keyboardTwo, "leetcode")); //73
        System.out.println(u.calculateTime(keyboardThree, wordThree)); //73



    }

}
